import React from 'react';

const Zoom = ({ selectedImg, setSelectedImg }) => {

    const click = (e) => {
       if (e.target.classList.contains('backdrop')){ 
        setSelectedImg(null);
        }
    }

    return (
        <div className="backdrop" onClick={click}>
            <img src={selectedImg} alt="enlarged pic" />

        </div>
    )


}

export default Zoom;
